map($.items', (data, index, state) => {
  return {
    id: index + 1,
    name: data.name,
    createdAt: state.cursor,
  };
});

map($.items, async (data, index, state) => {
  const userInfo = await fetchUserInfo(data.userId);
  return {
    id: index + 1,
    name: data.name,
    extra: userInfo,
  };
});
